import React, { useState } from 'react';
// import { Button, Navbar, Nav } from 'react-bootstrap';

import { LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom';
import { Modal, Row, Col } from 'react-bootstrap';

const Header = () => {
  // state to maintain modal for unlockWallet popup
  const [showModal, setShowModal] = useState(false);

  // Opening modal
  const openModal = () => {
    setShowModal(true);
  };

  // Close Modal
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <React.Fragment>
      <button class='play-button' onClick={() => openModal()}>
        Play
      </button>
      <ul className='non-bullet link-right'>
        <li className='list-inline home'>
          <a className='link' href='/'>
            Home
          </a>
        </li>
        <li class='list-inline farms'>
          <a class='link' href='/'>
            Farms
          </a>
        </li>
        {/* <li class='list-inline collect'>
          <a class='link' href='/'>
            Collect
          </a>
        </li> */}
        <Link className='list inline collect link' to='/collect'>
          Collect
        </Link>

        <li class='list-inline gov'>
          <a class='link' href='/'>
            Gov
          </a>
        </li>

        <li class='list-inline leaderboard'>
          <a class='link' href='/'>
            Leaderoard
          </a>
        </li>

        {/* <li class='list-inline'>
          <a class='link' href='/'>
            Tutorial
          </a>
        </li> */}
        <button className='wallet'>Unlock Wallet</button>
      </ul>

      {/* Modal  */}
      <div className='modal'>
        <Modal show={showModal} onHide={closeModal}>
          <Modal.Header>
            <Modal.Title>Unlock Wallet</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Row>
              <Col>MetaMask</Col>
            </Row>
            <Row>
              <Col>WalletConnect</Col>
            </Row>
          </Modal.Body>
        </Modal>
      </div>
    </React.Fragment>
  );
};

export default Header;
