import React, { useState } from 'react';
// import { Button, Navbar, Nav } from 'react-bootstrap';

import { LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom';

const Header = () => {
  // state to maintain modal for unlockWallet popup
  const [showModal, setShowModal] = useState(false);
  const openModal = () => setShowModal(true);
  const closeModal = () => setShowModal(false);
  // function of popping up the wallet
  const unlockWallet = () => {
    console.log('unlock wallet');
  };
  return (
    <div>
      <button class='play-button' onClick={() => unlockWallet()}>
        Play
      </button>
      <ul className='non-bullet link-right'>
        <li className='list-inline home'>
          <a className='link' href='/'>
            Home
          </a>
        </li>
        <li class='list-inline farms'>
          <a class='link' href='/'>
            Farms
          </a>
        </li>
        {/* <li class='list-inline collect'>
          <a class='link' href='/'>
            Collect
          </a>
        </li> */}
        <Link className='list inline collect link' to='/collect'>
          Collect
        </Link>

        <li class='list-inline gov'>
          <a class='link' href='/'>
            Gov
          </a>
        </li>

        <li class='list-inline leaderboard'>
          <a class='link' href='/'>
            Leaderoard
          </a>
        </li>

        {/* <li class='list-inline'>
          <a class='link' href='/'>
            Tutorial
          </a>
        </li> */}
        <button className='wallet'>Unlock Wallet</button>
      </ul>
    </div>
  );
};

export default Header;
